.login {
  width: 100%;
  height: 100%;
  flex-grow: 1;
}
.login .wrapper {
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.form {
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 400px;
  height: 300px;
  background-color: #373656;
  background-image: url("f1ec243e4f3119eb7ce3b1c24639557a.png");
  background-size: 450px 450px;
  background-position: -25px -125px;
  border-radius: 30px;
  box-shadow: 0px 0px 50px #000;
}
h1 {
  font-size: 22px;
}
.main-title {
  font-family: Quicksand;
  font-weight: 600;
/* letter-spacing: 1px;  */
/* font-style: italic; */
  color: #f97d75;
  text-shadow: 0px 10px 70px #000;
  margin-top: 40px;
  margin-bottom: 15px;
}
.input {
  width: 230px;
  height: 27px;
  margin: 3px 0px;
  padding: 5px 25px;
  background-repeat: no-repeat;
  background-size: 10px;
  background-position: 10px center;
  background-color: #4c4c71;
  color: #fff;
  border: none;
  border-radius: 100px;
}
:active,
:hover,
:focus {
  outline: 0;
  outline-offset: 0;
}
input:focus {
  box-shadow: 0px 0px 5px #000;
}
.input-nickname {
  background-image: url("d7a0eb3b1508aac03458207e73398389.png");
}
.input-name {
  background-image: url("95a70f9771b98fb141b2166d45d321e4.png");
  position: relative;
  left: -2px;
}
.input-password {
  background-image: url("c9dbcc8254aa3ec265b48922eff0a20c.png");
}
.input-confirm-password {
  background-image: url("c9dbcc8254aa3ec265b48922eff0a20c.png");
}
.input-email {
  background-image: url("d313bc2896089d976451f518318cd9ff.png");
}
.check {
  position: relative;
  top: 2.5px;
  right: 30px;
  width: 15px;
  height: 15px;
  margin-right: -15px;
}
.help:not(.active) {
  z-index: 100;
  position: absolute;
  opacity: 0;
  padding: 2px;
  transition: 1s all;
  left: var(--x);
  top: var(--y);
}
.help.active {
  z-index: 100;
  position: absolute;
  opacity: 1;
  color: #000;
  background-color: #fff;
  border-radius: 5px;
  padding: 4px;
  left: var(--x);
  top: var(--y);
}
.send {
  width: 150px;
  height: 40px;
  margin-top: 20px;
  color: #fff;
  font-family: arial;
  font-weight: 600;
  background: linear-gradient(to right, #ce6dfb, #6865f2);
  border-radius: 100px;
  border: none;
  text-align: center;
  vertical-align: middle;
  transition: 1s opacity;
  opacity: 1;
  cursor: pointer;
}
.send:not(.abled) {
  background: linear-gradient(to right, #ce6dfb, #6865f2);
  opacity: 0.1;
  cursor: auto;
}
